INSERT INTO `config_info` VALUES (1, 'admin-server-dev.yaml', 'DEFAULT_GROUP', 'spring:\r\n  application:\r\n    name: admin-server\r\n    # 数据库访问配置,主数据源，默认的\r\n  datasource:\r\n    type: com.alibaba.druid.pool.DruidDataSource\r\n    driver-class-name: com.mysql.cj.jdbc.Driver\r\n    url: jdbc:mysql://${MYSQL-HOST:localhost}:3306/yun-admin?useUnicode=true&characterEncoding=UTF-8&serverTimezone=GMT%2B8\r\n    username: lyj\r\n    password: 123456\r\n    # 初始化大小，最小，最大\r\n    druid:\r\n      # 初始连接数\r\n      initialSize: 5\r\n      # 最小连接池数量\r\n      minIdle: 10\r\n      # 最大连接池数量\r\n      maxActive: 20\r\n      # 配置获取连接等待超时的时间\r\n      maxWait: 60000\r\n      # 配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\r\n      timeBetweenEvictionRunsMillis: 60000\r\n      # 配置一个连接在池中最小生存的时间，单位是毫秒\r\n      minEvictableIdleTimeMillis: 300000\r\n      # 配置一个连接在池中最大生存的时间，单位是毫秒\r\n      maxEvictableIdleTimeMillis: 900000\r\n      # 配置检测连接是否有效\r\n      validationQuery: SELECT 1 FROM DUAL\r\n      testWhileIdle: true\r\n      testOnBorrow: false\r\n      testOnReturn: false\r\n      webStatFilter:\r\n        enabled: true\r\n      statViewServlet:\r\n        enabled: true\r\n        # 设置白名单，不填则允许所有访问\r\n        allow:\r\n        url-pattern: /druid/*\r\n      filter:\r\n        stat:\r\n          enabled: true\r\n          # 慢SQL记录\r\n          log-slow-sql: true\r\n          slow-sql-millis: 1000\r\n          merge-sql: true\r\n        wall:\r\n          config:\r\n            multi-statement-allow: true\r\n#mybatis-plus\r\nmybatis-plus:\r\n  # 如果是放在src/main/java目录下 classpath:/com/mistra/axela/*/mapper/*Mapper.xml\r\n  # 如果是放在resource目录 classpath:/mapper/*Mapper.xml\r\n  mapper-locations: classpath:/mapper/*.xml\r\n  #实体扫描，多个package用逗号或者分号分隔\r\n  typeAliasesPackage: com.lyj.admin.po\r\n  configuration:\r\n    #打印sql\r\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\r\n  global-config:\r\n    db-config:\r\n      logic-delete-value: 1 # 逻辑已删除值(默认为 1)\r\n      logic-not-delete-value: 0 # 逻辑未删除值(默认为 0)\r\n    #主键类型  0:\"数据库ID自增\", 1:\"用户输入ID\",2:\"全局唯一ID (数字类型唯一ID)\", 3:\"全局唯一ID UUID\";\r\n    id-type: 0\r\n    #字段策略 0:\"忽略判断\",1:\"非 NULL 判断\"),2:\"非空判断\"\r\n    field-strategy: 2\r\n    #驼峰下划线转换\r\n    db-column-underline: true\r\n    #刷新mapper 调试神器\r\n    #refresh-mapper: true\r\n    #数据库大写下划线转换\r\n    #capital-mode: true\r\n    #配置JdbcTypeForNull\r\n    jdbc-type-for-null: \'null\'\r\noauth2:\r\n  ignore:\r\n    #patterns: /**\r\n    patterns: /actuator/**,/webjars/**,/resources/**,/swagger-ui.html,/swagger-resources/**,/v2/api-docs\r\nswagger2:\r\n  basePackage: com.lyj.admin\r\n  title: \'后台管理系统API文档\'\r\n  version: \'1.0.0\'\r\n  description: \'后台管理服务\'\r\n  contact-name: \'湮灭\'\r\n  contact-url: \'http://132.232.17.78\'\r\n  contact-email: \'lyj5812@foxmail.com\'\r\nsecurity:\r\n  oauth2:\r\n    client:\r\n      client-id: admin\r\n      client-secret: 123456\r\n      access-token-uri: http://${AUTH-HOST:localhost}:8040/oauth/token\r\n      user-authorization-uri: http://${AUTH-HOST:localhost}:8040/oauth/authorize\r\n      client-authentication-scheme: form\r\n    resource:\r\n      user-info-uri: http://${AUTH-HOST:localhost}:8040/user/info\r\n      token-info-uri: http://${AUTH-HOST:auth-server}:8040/oauth/check_token\r\n      prefer-token-info: false', 'e4594c92056458be2e837199122dfc69', '2019-05-27 17:27:31', '2019-11-25 16:36:37', NULL, '121.239.57.19', '', '', '', 'null', 'null', 'yaml', 'null');
