INSERT INTO `his_config_info` VALUES (1, 24, 'admin-server-dev.yaml', 'DEFAULT_GROUP', '', 'spring:\r\n  application:\r\n    name: admin-server\r\n    # 数据库访问配置,主数据源，默认的\r\n  datasource:\r\n    type: com.alibaba.druid.pool.DruidDataSource\r\n    driver-class-name: com.mysql.cj.jdbc.Driver\r\n    url: jdbc:mysql://47.52.79.108:3306/auth?useUnicode=true&characterEncoding=UTF-8&serverTimezone=GMT%2B8\r\n    username: root\r\n    password: root\r\n    # 初始化大小，最小，最大\r\n    druid:\r\n      # 初始连接数\r\n      initialSize: 5\r\n      # 最小连接池数量\r\n      minIdle: 10\r\n      # 最大连接池数量\r\n      maxActive: 20\r\n      # 配置获取连接等待超时的时间\r\n      maxWait: 60000\r\n      # 配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\r\n      timeBetweenEvictionRunsMillis: 60000\r\n      # 配置一个连接在池中最小生存的时间，单位是毫秒\r\n      minEvictableIdleTimeMillis: 300000\r\n      # 配置一个连接在池中最大生存的时间，单位是毫秒\r\n      maxEvictableIdleTimeMillis: 900000\r\n      # 配置检测连接是否有效\r\n      validationQuery: SELECT 1 FROM DUAL\r\n      testWhileIdle: true\r\n      testOnBorrow: false\r\n      testOnReturn: false\r\n      webStatFilter:\r\n        enabled: true\r\n      statViewServlet:\r\n        enabled: true\r\n        # 设置白名单，不填则允许所有访问\r\n        allow:\r\n        url-pattern: /druid/*\r\n      filter:\r\n        stat:\r\n          enabled: true\r\n          # 慢SQL记录\r\n          log-slow-sql: true\r\n          slow-sql-millis: 1000\r\n          merge-sql: true\r\n        wall:\r\n          config:\r\n            multi-statement-allow: true\r\n#mybatis-plus\r\nmybatis-plus:\r\n  # 如果是放在src/main/java目录下 classpath:/com/mistra/axela/*/mapper/*Mapper.xml\r\n  # 如果是放在resource目录 classpath:/mapper/*Mapper.xml\r\n  mapper-locations: classpath:/mapper/*.xml\r\n  #实体扫描，多个package用逗号或者分号分隔\r\n  typeAliasesPackage: com.lyj.admin.po\r\n  configuration:\r\n    #打印sql\r\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\r\n  global-config:\r\n    db-config:\r\n      logic-delete-value: 1 # 逻辑已删除值(默认为 1)\r\n      logic-not-delete-value: 0 # 逻辑未删除值(默