除值(默认为 0)\r\n    #主键类型  0:\"数据库ID自增\", 1:\"用户输入ID\",2:\"全局唯一ID (数字类型唯一ID)\", 3:\"全局唯一ID UUID\";\r\n    id-type: 0\r\n    #字段策略 0:\"忽略判断\",1:\"非 NULL 判断\"),2:\"非空判断\"\r\n    field-strategy: 2\r\n    #驼峰下划线转换\r\n    db-column-underline: true\r\n    #刷新mapper 调试神器\r\n    #refresh-mapper: true\r\n    #数据库大写下划线转换\r\n    #capital-mode: true\r\n    #配置JdbcTypeForNull\r\n    jdbc-type-for-null: \'null\'\r\noauth2:\r\n  ignore:\r\n    #patterns: /**\r\n    patterns: /user/findByUsername/**,/role/getRoleByUserId/**,/menu/menuTree/**,/menu/btnPermissions/**,/actuator/**,/webjars/**,/resources/**,/swagger-ui.html,/swagger-resources/**,/v2/api-docs\r\nswagger2:\r\n  basePackage: com.lyj.admin\r\n  title: \'后台管理系统API文档\'\r\n  version: \'0.7\'\r\n  description: \'无\'\r\n  contact-name: \'刘云杰\'\r\n  contact-url: \'http://114.67.71.138\'\r\n  contact-email: \'1264415695@qq.com\'\r\nsecurity:\r\n  oauth2:\r\n    client:\r\n      client-id: admin\r\n      client-secret: 123456\r\n      access-token-uri: http://${AUTH-HOST:localhost}:8040/oauth/token\r\n      user-authorization-uri: http://${AUTH-HOST:localhost}:8040/oauth/authorize\r\n      client-authentication-scheme: form\r\n    resource:\r\n      user-info-uri: http://${AUTH-HOST:localhost}:8040/user/info\r\n      token-info-uri: http://${AUTH-HOST:auth-server}:8040/oauth/check_token\r\n      prefer-token-info: false', '5f36fa6b421ce5df3c07400836056eef', '2010-05-05 00:00:00', '2019-11-14 13:43:55', NULL, '121.239.57.19', 'U', '6c330126-7080-45b8-8374-9d334d7903be');
